import React, { useState } from 'react';
import axios from 'axios';
import logo from "./images/logo.svg"; // Update this path
import { useNavigate } from 'react-router-dom';

function CreatePost() {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!title.trim() || !content.trim()) {
      setError('Title and content cannot be empty.');
      return;
    }
    setError('');
    setSuccess('');

    const token = localStorage.getItem('token');

    if (!token) {
      setError('No authentication token found. Please login.');
      return;
    }

    try {
      await axios.post('http://localhost:5500/create-post', { title, content }, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      setSuccess('Post created successfully');
      navigate('/forum'); // Redirect to the forum
    } catch (error) {
      console.error('Error creating post:', error);
      setError('Error creating post. Please try again.');
    }
  };

  const handleCancel = () => {
    navigate('/forum'); // Redirect to the forum
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <img src={logo} alt='Logo' style={{ width: '150px' }} />
      <h2>Create a Post</h2>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {success && <p style={{ color: 'green' }}>{success}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <input
            type="text"
            id="title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            style={{ border: '1px solid red', borderRadius: '4px' }}
          />
        </div>
        <div>
          <textarea
            id="content"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            style={{ border: '1px solid red', borderRadius: '4px' }}
          />
        </div>
        <button type="submit" style={{ backgroundColor: 'red', color: 'white', margin: '10px' }}>
          Cr√©er
        </button>
        <p onClick={handleCancel} style={{ color: 'red', cursor: 'pointer' }}>
          Annuler
        </p>
      </form>
    </div>
  );
}

export default CreatePost;
