import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Account() {
  const [userInfo, setUserInfo] = useState({});

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await axios.get('http://localhost:5500/user-info', {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        // Log the response to see if data is received
        console.log('Response:', response);

        setUserInfo(response.data);
      } catch (error) {
        console.error('Error fetching user info:', error);
        // Handle error (e.g., redirect to login if token is invalid)
      }
    };

    fetchUserInfo();
  }, []);

  const handleGw2ApiKeyChange = (e) => {
    setGw2ApiKey(e.target.value);
  };

  const fetchGw2AccountInfo = async () => {
    try {
      const response = await axios.get('https://api.guildwars2.com/v2/account', {
        headers: { 'Authorization': `Bearer ${gw2ApiKey}` }
      });
      setGw2AccountInfo(response.data);
    } catch (error) {
      console.error('Error fetching GW2 account info:', error);
      // Handle error (e.g., show an error message to the user)
    }
  };

  return (
    <div>
      <h1>Account Page</h1>
      <p>Name: {userInfo.name}</p>
      {/* Display other user information as needed */}

      <div>
        <input
          type="text"
          placeholder="Enter your GW2 API Key"
          value={gw2ApiKey}
          onChange={handleGw2ApiKeyChange}
        />
        <button onClick={fetchGw2AccountInfo}>Fetch GW2 Account Info</button>
      </div>

      {gw2AccountInfo && (
        <div>
          <h2>GW2 Account Information</h2>
          {/* Display GW2 account information */}
          <p>ID: {gw2AccountInfo.id}</p>
          <p>Name: {gw2AccountInfo.name}</p>
          {/* Add more fields as needed */}
        </div>
      )}
    </div>
  );
}


export default Account;
