import React, { useState } from "react";
import logo from "./images/logo.svg";
import { Link } from "react-router-dom";
import Validation from './signupValidation'
import axios from 'axios'
import { useNavigate } from 'react-router-dom';

function Signup() {
  const [values, setValues] = useState({
    name: '',
    email: '',
    password: ''
  });

  const navigate = useNavigate();
  const [errors, setErrors] = useState({});

  const handleInput = (event) => {
    setValues(prev => ({...prev, [event.target.name]: event.target.value}));
  }

  const handleSubmit = async (event) => {
    //console.log(values);
    event.preventDefault();
    const newErrors = Validation(values);
    setErrors(newErrors);

    if(newErrors.name === "" && newErrors.email === "" && newErrors.password === ""){
      try {
        const res = await axios.post('http://localhost:5500/signup', values);
        navigate('/');
      } catch (err) {
        console.log(err);
      }
    }
  }
  return (
    <div>
      <img class="w-25" src={logo} alt="logo" />
      <div class="d-flex justify-content-center align-items-center bg-gray vh-100 vw_100">
        <div>
          <form action="" onSubmit={handleSubmit}>
            <div class="p-3 border-0">
              <input
                class="placeholderss text-danger text-center p-1 mx-auto rounded-4 border border-danger"
                type="text"

                name='name'
                placeholder="Pseudo"
                onChange={handleInput}
              />
              {errors.name && <span class='text-danger'>{errors.name}</span>}
            </div>

            <div class="p-3 border-0">
              <input
                class="placeholderss text-danger text-center p-1 mx-auto rounded-4 border border-danger"
                type="email"
                name='email'
                placeholder="Adresse Email"
                onChange={handleInput}
              />
              {errors.email && <span class='text-danger'>{errors.email}</span>}
            </div>


            <div class=" w-100 p-3 mb-6 ">
              <input
                class="placeholderss text-danger text-center p-1 mx-auto rounded-4 border border-danger"
                type="password"
                name='password'
                placeholder="Votre mot de passe"
                onChange={handleInput}
              />
              {errors.password && <span class='text-danger'>{errors.password}</span>}
            </div>

            <div class="d-flex align-items-center  flex-column">
              <button type='submit' class="w-50 button_co d-block btn rounded-4 btn-succes text-white bg-danger text-white ">
                S'enregistrer
              </button>

              <Link to="/" class="btn btn-default text-danger">
                Se connecter
              </Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Signup;
