import React, { useState } from "react";
import logo from "./images/logo.svg";
import { Link } from "react-router-dom";
import Validation from './signupValidation'
import axios from 'axios'

function Signup() {
    const [values, setValues] = useState({
        name: '',
        email: '',
        password: ''
    }
    )

const navigate = useNavigate();
const [errors, setErrors] = useState({})





    const handleInput = (event) => { 
        setValues(prev => ({...prev, [event.target.name]: [event.target.value]}))
    }

    const handleSubmit =(event) => {
        event.preventDefault();
        setErrors(Validation(values));
        if(errors.name === "" && errors.email === "" && errors.password ===""){
            axios.post('http://localhost:8081/signup',values)
            .then(res => {
                navigate('/');
        })
            .catch(err => console.log(err));
        }

    }
  return (
    <div>
      <img class="w-25" src={logo} alt="logo" />
      <div className="d-flex justify-content-center align-items-center bg-gray vh-100 vw_100">
        <div>
          <form action="" onSubmit={handleSubmit}>
            <div className="p-3 border-0">
              <input
                class="placeholderss text-danger text-center p-1 mx-auto rounded-4 border border-danger"
                type="text"

                name='name'
                placeholder="Pseudo"
                onChange={handleInput}
              />
              {errors.name && <span className='text-danger'>{errors.name}</span>}
            </div>

            <div className="p-3 border-0">
              <input
                class="placeholderss text-danger text-center p-1 mx-auto rounded-4 border border-danger"
                type="email"
                name='email'
                placeholder="Adresse Email"
                onChange={handleInput}
              />
              {errors.email && <span className='text-danger'>{errors.email}</span>}
            </div>


            <div className=" w-100 p-3 mb-6 ">
              <input
                class="placeholderss text-danger text-center p-1 mx-auto rounded-4 border border-danger"
                type="password"
                name='password'
                placeholder="Votre mot de passe"
                onChange={handleInput}
              />
              {errors.password && <span className='text-danger'>{errors.password}</span>}
            </div>

            <div className="d-flex align-items-center  flex-column">
              <button type='submit' className="w-50 button_co d-block btn rounded-4 btn-succes text-white bg-danger text-white ">
                Se connecter
              </button>

              <Link to="/" className="btn btn-default text-danger">
                Se connecter
              </Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Signup;
