import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Account() {
    const [userInfo, setUserInfo] = useState({});
    const [gw2ApiKey, setGw2ApiKey] = useState('');
    const [gw2AccountInfo, setGw2AccountInfo] = useState(null);

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await axios.get('http://localhost:5500/user-info', {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        // Log the response to see if data is received
        console.log('Response:', response);

        setUserInfo(response.data);
      } catch (error) {
        console.error('Error fetching user info:', error);
        // Handle error (e.g., redirect to login if token is invalid)
      }
    };

    fetchUserInfo();
  }, []);

  const handleGw2ApiKeyChange = (e) => {
    setGw2ApiKey(e.target.value);
  };

  const fetchGw2AccountInfo = async () => {
    try {
      const url = `https://api.guildwars2.com/v2/account?access_token=${encodeURIComponent(gw2ApiKey)}`;
      const response = await axios.get(url);
      // Directly set the GW2 account info without additional manipulation
      setGw2AccountInfo(response.data);
    } catch (error) {
      console.error('Error fetching GW2 account info:', error);
      // Handle error (e.g., show an error message to the user)
    }
  };

  return (
    <div>
      <h1>Account Page</h1>
      {/* Display user name and GW2 account name if available */}
      <p>Name: {userInfo.name} {gw2AccountInfo && `/ ${gw2AccountInfo.name}`}</p>

      <div>
        <input
          type="text"
          placeholder="Enter your GW2 API Key"
          value={gw2ApiKey}
          onChange={handleGw2ApiKeyChange}
        />
        <button onClick={fetchGw2AccountInfo}>Fetch GW2 Account Info</button>
      </div>

      {/* Render other information if needed */}
    </div>
  );
}

export default Account;
