import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import logo from "./images/logo.svg"; // Ensure this path is correct
import jwt_decode from 'jwt-decode';

function DisplayPosts() {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPosts = async () => {
      // ... existing fetching code ...
    };

    fetchPosts();
  }, []);

  const handleDeletePost = async (postId) => {
    // Logic to delete post
    try {
      const token = localStorage.getItem('token');
      await axios.delete(`http://localhost:5500/posts/${postId}`, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      // Remove the post from the state
      setPosts(posts.filter(post => post.id !== postId));
    } catch (error) {
      console.error('Error deleting post:', error);
      // Handle delete error
    }
  };
  const token = localStorage.getItem('token');
  let currentUserId = null;

  if (token) {
    try {
      const decodedToken = jwt_decode(token);
      currentUserId = decodedToken.userId; // Replace 'userId' with the appropriate field name based on your token structure
    } catch (error) {
      console.error('Error decoding token:', error);
    }
  }

  return (
    <div>
      {/* Header */}
      <div style={{ height: '8vh', width: '100%', background: 'white', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 5vw' }}>
        <img src={logo} alt='Logo' style={{ maxHeight: '6vh' }} /> {/* Smaller logo */}
        <span onClick={() => navigate('/account')} style={{ cursor: 'pointer' }}>Mon compte</span>
      </div>

      {/* Banner */}
      <div style={{ width: '100%', height: '10vh', background: 'lightgrey', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <h1 style={{ color: 'red', margin: 0 }}>Bienvenue dans mon forum GW2</h1>
      </div>

      {/* Discussions Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '5vh', marginLeft: '5vw', marginRight: '5vw' }}>
        <h2 style={{ margin: 0 }}>Discussions</h2>
        <span onClick={navigateToAccount} style={{ color: 'red', fontSize: '24px', cursor: 'pointer' }}>ï¼‹</span>
      </div>

      {/* Red Line */}
      <hr style={{ borderColor: 'red', margin: '5vw 50% 5vw 5vw' }} />

      {/* Posts */}
      {posts.map((post, index) => (
  <div key={post.id} style={{ marginLeft: '10vw', marginRight: '5vw' }}>
    {/* ... post content ... */}
    {post.userId === currentUserId && (
      <button onClick={() => handleDeletePost(post.id)} style={{ color: 'red', borderColor: 'red', cursor: 'pointer' }}>Delete Post</button>
    )}
    {index !== posts.length - 1 && <hr style={{ borderColor: 'red' }} />}
  </div>
))}

      {/* Footer */}
      <div style={{ width: '100%', background: 'red', color: 'white', textAlign: 'center', padding: '1vh 0' }}>
        {/* Footer content */}
        <p>Contact: lpeyramaure@eduvaud.ch</p>
        <p>Nombre d'inscrits: 451</p>
        <p>Nombre de posts: 645</p>
      </div>
    </div>
  );
}

export default DisplayPosts;
