import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import logo from "./images/logo.svg"; // Ensure this path is correct

/**
 * Component for displaying posts.
 *
 * @returns {JSX.Element} The DisplayPosts component.
 */
/**
 * Renders a component that displays posts and allows users to create and delete posts.
 *
 * @returns {JSX.Element} The DisplayPosts component.
 */
function DisplayPosts() {
    const [posts, setPosts] = useState([]);
    const navigate = useNavigate();

    // Decode JWT to get the current user ID
    const token = localStorage.getItem('token');
    let currentUserId = null;
    if (token) {
        try {
          const decodedToken = jwtDecode(token);
          currentUserId = decodedToken.userId;
          console.log('Decoded Token:', decodedToken); // Add this line
        } catch (error) {
          console.error('Error decoding token:', error);
        }
    }

    useEffect(() => {
      const fetchPosts = async () => {
        try {
          const response = await axios.get('http://localhost:5500/get-posts');
          setPosts(response.data);
        } catch (error) {
          console.error('Error fetching posts:', error);
        }
      };

      fetchPosts();
    }, []);

    const navigateToAccount = () => {
      navigate('/create-post');
    };

    const handleDeletePost = async (postId) => {
      try {
        if (!token) {
          console.error('Authentication token not found');
          return;
        }

        await axios.delete(`http://localhost:5500/posts/${postId}`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        setPosts(posts.filter(post => post.id !== postId));
      } catch (error) {
        console.error('Error deleting post:', error);
      }
    };

    const mainContainerStyle = {
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'space-between'
    };

    // Add other styles as needed
    const postStyle = {
        marginLeft: '10vw', 
        marginRight: '10vw', 
        marginBottom: '20px',
        // Add more styles for posts here
    };

    const postHeaderStyle = {
        color: 'red', 
        cursor: 'pointer'
        // Add more styles for post header here
    };

    const postContentStyle = {
        color: '#000'
        // Add more styles for post content here
    };

    const userInfoStyle = {
        display: 'flex', 
        alignItems: 'center',
        justifyContent: 'flex-end', // Align items to the right
        marginRight: '5vw'
        // Add more styles for user info here
    };

    const userImageStyle = {
        width: '50px', // Increased size
        height: '50px', // Increased size
        borderRadius: '50%', 
        marginLeft: '10px' // Add margin to separate image from name
        // Add more styles for user image here
    };

    const deleteButtonStyle = {
        backgroundColor: 'transparent', 
        color: 'red', 
        border: 'none', 
        cursor: 'pointer', 
        fontSize: '20px'
        // Add more styles for delete button here
    };

    const hrStyle = {
        borderColor: 'red', 
        marginTop: '10px', 
        marginBottom: '10px', 
        width: '50%'
        // Add more styles for horizontal rule here
    };


  return (
    <div style={mainContainerStyle}>
      {/* Header */}
      <div style={{ height: '8vh', width: '100%', background: 'white', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 5vw' }}>
        <img src={logo} alt='Logo' style={{ maxHeight: '6vh' }} />
        <span onClick={() => navigate('/account')} style={{ cursor: 'pointer', color: 'red' }}>Mon compte</span>
      </div>

      {/* Banner */}
      <div style={{ width: '100%', height: '10vh', background: 'lightgrey', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <h1 style={{ color: 'red', margin: 0 }}>Bienvenue dans mon forum GW2</h1>
      </div>

      {/* Discussions Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '5vh', marginBottom: '5vh', marginLeft: '5vw', marginRight: '5vw' }}> {/* Updated margin bottom */}
                <h2 style={{ margin: 0 }}>Discussions</h2>

                <span onClick={navigateToAccount} style={{ color: 'red', fontSize: '24px', cursor: 'pointer' }}>＋</span>
            </div>

      {/* Posts */}
      {posts.map((post) => (
                <div key={post.id} style={postStyle}>
                    <h3 style={postHeaderStyle} onClick={() => navigate(`/posts/${post.id}`)}>
                        {post.title}
                    </h3>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <p style={postContentStyle}>{post.content.substring(0, 100)}...</p>
                        <div style={userInfoStyle}>
                            <span>{post.userName} / {post.gw2_account_name}</span>
                            {post.compteImage && (
                                <img
                                    src={`http://localhost:5500/${post.compteImage}`}
                                    alt="User avatar"
                                    style={userImageStyle}
                                />
                            )}
                        </div>
                    </div>
                    {currentUserId && post.user_id === currentUserId && (
                        <button 
                            onClick={() => handleDeletePost(post.id)} 
                            style={deleteButtonStyle}
                            title="Supprimer poste"
                        >
                            ❌
                        </button>
                    )}
                    {posts.length - 1 !== posts.indexOf(post) && (
                        <hr style={hrStyle} />
                    )}
                </div>
            ))}




           {/* Footer */}
           <div style={{ backgroundColor: 'red', color: 'white', textAlign: 'center', padding: '1vh 0', width: '100%', display: 'flex', flexDirection: 'column' }}>
                <p>Contact: lpeyramaure@eduvaud.ch</p>
                <p>Nombre d'inscrits: 451</p>
                <p>Nombre de posts: 645</p>
            </div>
        </div>
        );
    }

export default DisplayPosts;
