import React, { useState, useEffect } from 'react';
import axios from 'axios';




function Account() {
  const [userInfo, setUserInfo] = useState({});
  const [gw2ApiKey, setGw2ApiKey] = useState('');
  const [gw2AccountInfo, setGw2AccountInfo] = useState(null);

  useEffect(() => {
    const fetchUserInfo = async () => {
      const token = localStorage.getItem('token');
      try {
        const response = await axios.get('http://localhost:5500/user-info', {
          headers: { 'Authorization': `Bearer ${token}` }
        });
        console.log('Response:', response);
        setUserInfo(response.data);
      } catch (error) {
        console.error('Error fetching user info:', error);
      }
    };
    fetchUserInfo();
  }, []);

  const handleGw2ApiKeyChange = (e) => {
    setGw2ApiKey(e.target.value);
  };

  const fetchGw2AccountInfo = async () => {
    try {
      const url = `https://api.guildwars2.com/v2/account?access_token=${encodeURIComponent(gw2ApiKey)}`;
      const response = await axios.get(url);
      setGw2AccountInfo(response.data);

      // Check if GW2 account name is already stored in the database
      // If not, update it
      if (!userInfo.gw2AccountName) {
        await updateGw2AccountName(response.data.name);
      }
    } catch (error) {
      console.error('Error fetching GW2 account info:', error);
    }
  };


  // Function to update GW2 account name in your database
  const updateGw2AccountName = async (gw2AccountName) => {
    try {
      const token = localStorage.getItem('token');
      await axios.post('http://localhost:5500/update-gw2-account-name', { gw2AccountName }, {
        headers: { 'Authorization': `Bearer ${token}` }
      });
      console.log('GW2 account name updated successfully');
      // Optionally update the local state to reflect the saved GW2 account name
    } catch (error) {
      console.error('Error updating GW2 account name:', error);
    }
  };

  return (
        <div>
          <h1>Page de compte</h1>
          <p>{userInfo.name} {userInfo.gw2AccountName || (gw2AccountInfo && `/ ${gw2AccountInfo.name}`)}</p>
      <div>
        <input
          type="text"
          placeholder="Enter your GW2 API Key"
          value={gw2ApiKey}
          onChange={handleGw2ApiKeyChange}
        />
        <button onClick={fetchGw2AccountInfo}>Obtenir mon nom de compte joueur</button>
      </div>
    </div>
  );


}

export default Account;
