import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import logo from "./images/logo.svg";
import { jwtDecode } from 'jwt-decode';

function PostDetail() {
    const [post, setPost] = useState(null);
    const [responses, setResponses] = useState([]);
    const [responseContent, setResponseContent] = useState('');
    const { postId } = useParams();
    const navigate = useNavigate();

    const navigateToForum = () => {
        navigate('/forum'); // Navigate to /forum route
    };
    useEffect(() => {
        const fetchPostAndResponses = async () => {
            try {
                const { data } = await axios.get(`http://localhost:5500/posts/${postId}`);
                setPost(data.post);
                setResponses(data.responses);
            } catch (error) {
                console.error('Error fetching post and responses:', error);
            }
        };

        fetchPostAndResponses();
    }, [postId]);


    const token = localStorage.getItem('token');
    let userId = null;
    if (token) {
        const decodedToken = jwtDecode(token);
        userId = decodedToken.userId;
    }

    const handleSubmitResponse = async (e) => {
        e.preventDefault();
        const trimmedContent = responseContent.trim();

        if (!trimmedContent) {
            alert('la réponse ne doit pas être vide');
            return;
        }

        try {
            await axios.post(`http://localhost:5500/posts/${postId}/responses`, { content: trimmedContent }, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            setResponses([...responses, { content: trimmedContent }]);
            setResponseContent('');
        } catch (error) {
            console.error('Error submitting response:', error);
        }
    };

    const handleDeleteResponse = async (responseId) => {
        try {
            await axios.delete(`http://localhost:5500/responses/${responseId}`, {
                headers: { 'Authorization': `Bearer ${token}` }
            });
            setResponses(responses.filter(response => response.id !== responseId));
        } catch (error) {
            console.error('Error deleting response:', error);
        }
    };

    if (!post) {
        return <div>Loading...</div>;
    }
    const userImageStyle = {
        height: '50px',
        marginRight: '20px',
        borderRadius: '50%',
        alignSelf: 'center' // Center image vertically
    };

    const postContainerStyle = {
        display: 'flex',
        alignItems: 'flex-start',
        justifyContent: 'flex-start', // Align items to the start of the flex container
        marginBottom: '10px',
        gap: '20px' // Add space between image and text
    };

    const textContentStyle = {
        flex: 1, // Take up remaining space
        marginLeft: '20px' // Align text further right
    }

    const contentStyle = {
        flex: 1 // Take up remaining space
    };

    const buttonStyle = {
        backgroundColor: 'transparent',
        color: 'red',
        border: 'none',
        cursor: 'pointer',
        fontSize: '20px',
        alignSelf: 'center' // Center button vertically
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
            {/* Main Content */}
            <div style={{ flex: 1, margin: '0 5vw' }}>
                {/* Header */}
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '20px', marginTop: '20px' }}>
                    <img src={logo} alt='Logo' style={{ height: '50px', cursor: 'pointer' }} onClick={navigateToForum} />
                    <span onClick={() => navigate('/account')} style={{ color: 'red', cursor: 'pointer' }}>Mon compte</span>
                </div>

                {/* Post Title and Details */}
                <div style={postContainerStyle}>
        {post.compteImage && (
            <img
                src={`http://localhost:5500/${post.compteImage}`}
                alt={`${post.userName}'s avatar`}
                style={userImageStyle}
            />
        )}
        <div style={textContentStyle}>
            <h1 style={{ color: 'red' }}>{post.title}</h1>
            <p><strong>{post.userName} / {post.gw2_account_name}</strong></p>
            <p>{post.content}</p>
        </div>
    </div>

                {/* Responses */}
                {responses.map((response) => (
                    <div key={response.id} style={postContainerStyle}>
                        {response.compteImage && (
                            <img
                                src={`http://localhost:5500/${response.compteImage}`}
                                alt={`${response.name}'s avatar`}
                                style={userImageStyle}
                            />
                        )}
                        <div style={contentStyle}>
                            <p><strong>{response.name} / {response.gw2_account_name}</strong></p>
                            <p>{response.content}</p>
                        </div>
                        {response.user_id === userId && (
                            <button 
                                onClick={() => handleDeleteResponse(response.id)}
                                style={buttonStyle}
                                title="Delete Response"
                            >
                                ❌
                            </button>
                        )}
                    </div>
                ))}


                {/* Response Form */}
                <form onSubmit={handleSubmitResponse} style={{ marginTop: '20px' }}>
                    <textarea
                        value={responseContent}
                        onChange={(e) => setResponseContent(e.target.value)}
                        style={{ width: '100%', height: '100px', marginBottom: '10px', border: '1px solid red', borderRadius: '10px' }}
                    />
                    <button type="submit" style={{ display: 'flex', justifyContent: 'center', backgroundColor: 'white', borderRadius: '10px', border: '1px solid red', color: 'red', margin:'auto' }}>Poster réponse</button>
                </form>
            </div>

            {/* Footer */}
            <div style={{ backgroundColor: 'red', color: 'white', textAlign: 'center', padding: '1vh 0', width: '100%' }}>
                <p>Contact: lpeyramaure@eduvaud.ch</p>
                <p>Nombre d'inscrits: 451</p>
                <p>Nombre de posts: 645</p>
            </div>
        </div>
    );

                        }    

export default PostDetail;
