import React, { useState } from "react";
import logo from "./images/logo.svg";
import { Link } from "react-router-dom"
import Validation from "./LoginValidation";
import axios from 'axios';
import { useNavigate } from 'react-router-dom';


function Login() {
  const [values, setValues] = useState({
      email: '',
      password: ''
  });
  const [errors, setErrors] = useState({});
  const navigate = useNavigate(); // Use this for navigation

  const handleInput = (event) => { 
      setValues({...values, [event.target.name]: event.target.value});
  };

  const handleSubmit = async (event) => {
      event.preventDefault();
      const validationErrors = Validation(values);
      setErrors(validationErrors);

      if (Object.keys(validationErrors).length === 0) {
          try {
              const response = await axios.post('http://localhost:5500/login', values);
              localStorage.setItem('token', response.data.token);
              localStorage.setItem('username', response.data.username); // Assuming username is part of response
              navigate('/account'); // Redirect to account page using navigate
          } catch (error) {
              // Handle errors (e.g., incorrect credentials)
          }
      }
  };
  return (
    <div>
        <img class="w-25"src={logo} alt="logo"/>
      <div class="d-flex justify-content-center align-items-center bg-gray vh-100 vw_100">
        <div>
          <form action="" onSubmit={handleSubmit}>
            <div class="p-3 border-0">
              <input
                class="placeholderss text-danger text-center p-1 mx-auto rounded-4 border border-danger"
                type="email"
                placeholder="Adresse Email"
                name='email'
                onChange={handleInput}
              />
              {errors.email && <span class='text-danger'>{errors.email}</span>}
            </div>

            <div class=" w-100 p-3 mb-6 ">
              <input
                class="placeholderss text-danger text-center p-1 mx-auto rounded-4 border border-danger"
                type="password"
                placeholder="Votre mot de passe"
                name='password'
                onChange={handleInput}
              />
             {errors.password && <span class='text-danger'>{errors.password}</span>}

            </div>
            <div class="d-flex align-items-center  flex-column">
              <button type='subtmit' class="w-50 button_co d-block btn rounded-4 btn-succes text-white bg-danger text-white ">
                Se connecter
              </button>

              <Link to="/signup" class="btn btn-default text-danger">
                Cr√©er un compte
              </Link>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Login;
