import React, { useState, useEffect } from 'react';
import axios from 'axios';

function DisplayPosts() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5500/get-posts');
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
        // You should handle this error more gracefully in a user-facing app
      }
    };

    fetchPosts();
  }, []);

  return (
    <div style={{ maxWidth: '800px', margin: 'auto' }}>
      <h1 style={{ textAlign: 'center' }}>Discussions</h1>
      {posts.map(post => (
        <div key={post.id} style={{
          border: '1px solid #ccc',
          padding: '10px',
          marginBottom: '10px',
          borderRadius: '5px'
        }}>
          <h2>{post.title}</h2>
          <p style={{ color: '#555', margin: '5px 0' }}>{post.content}</p>
          <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <span>{post.userName}</span>
            <span>{post.gw2_account_name}</span>
          </div>
        </div>
      ))}
    </div>
  );
}

export default DisplayPosts;
