import React, { useState, useEffect } from "react";
import axios from "axios";

function Account() {
  const [userInfo, setUserInfo] = useState({});
  const [gw2ApiKey, setGw2ApiKey] = useState("");
  const [gw2AccountInfo, setGw2AccountInfo] = useState(null);
  const [image, setImage] = useState(null);

  useEffect(() => {
    const fetchUserInfoAndImage = async () => {
      const token = localStorage.getItem("token");
      try {
        const userInfoResponse = await axios.get(
          "http://localhost:5500/user-info",
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );
        setUserInfo(userInfoResponse.data);

        // Fetch the user image
        if (userInfoResponse.data.id) {
          const userImageResponse = await axios.get(
            `http://localhost:5500/get-user-image/${userInfoResponse.data.id}`,
            {
              headers: { Authorization: `Bearer ${token}` },
            }
          );
          setUserInfo({
            ...userInfoResponse.data,
            compteImage: `data:image/jpeg;base64,${userImageResponse.data}`,
          });
        }
      } catch (error) {
        console.error("Error fetching user info or image:", error);
      }
    };
    fetchUserInfoAndImage();
  }, []);

  const handleGw2ApiKeyChange = (e) => {
    setGw2ApiKey(e.target.value);
  };

  // ... existing GW2 account info functions ...

  const handleImageChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      setImage(e.target.files[0]);
    }
  };

  const handleSubmitImage = async () => {
    const formData = new FormData();
    formData.append("image", image);

    const token = localStorage.getItem("token");
    try {
      await axios.post("http://localhost:5500/upload-image", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
      });
      console.log("Image uploaded successfully");
      // Refresh the user info to show the updated image
      fetchUserInfoAndImage();
    } catch (error) {
      console.error("Error uploading image:", error);
    }
  };

  return (
    <div>
      <h1>Page de compte</h1>
      <p>
        {userInfo.name}{" "}
        {userInfo.gw2AccountName ||
          (gw2AccountInfo && `/ ${gw2AccountInfo.name}`)}
      </p>

      {/* User Image Display */}
      {userInfo.compteImage && (
        <img
          src={userInfo.compteImage}
          alt="User"
          style={{ height: "100px" }}
        />
      )}

      {/* GW2 API Key Input */}
      <div>
        <input
          type="text"
          placeholder="Enter your GW2 API Key"
          value={gw2ApiKey}
          onChange={handleGw2ApiKeyChange}
        />
        <button onClick={fetchGw2AccountInfo}>
          Obtenir mon nom de compte joueur
        </button>
      </div>

      {/* Image Upload */}
      <div>
        <input type="file" onChange={handleImageChange} />
        <button onClick={handleSubmitImage}>Upload Image</button>
      </div>
    </div>
  );
}

export default Account;
