import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { jwtDecode } from 'jwt-decode';
import logo from "./images/logo.svg"; // Ensure this path is correct

/**
 * Component for displaying posts.
 *
 * @returns {JSX.Element} The DisplayPosts component.
 */
/**
 * Renders a component that displays posts and allows users to create and delete posts.
 *
 * @returns {JSX.Element} The DisplayPosts component.
 */
function DisplayPosts() {
    const [posts, setPosts] = useState([]);
    const navigate = useNavigate();

    // Decode JWT to get the current user ID
    const token = localStorage.getItem('token');
    let currentUserId = null;
    if (token) {
        try {
          const decodedToken = jwtDecode(token);
          currentUserId = decodedToken.userId;
          console.log('Decoded Token:', decodedToken); // Add this line
        } catch (error) {
          console.error('Error decoding token:', error);
        }
    }

    useEffect(() => {
      const fetchPosts = async () => {
        try {
          const response = await axios.get('http://localhost:5500/get-posts');
          setPosts(response.data);
        } catch (error) {
          console.error('Error fetching posts:', error);
        }
      };

      fetchPosts();
    }, []);

    const navigateToAccount = () => {
      navigate('/create-post');
    };

    const handleDeletePost = async (postId) => {
      try {
        if (!token) {
          console.error('Authentication token not found');
          return;
        }

        await axios.delete(`http://localhost:5500/posts/${postId}`, {
          headers: { 'Authorization': `Bearer ${token}` }
        });

        setPosts(posts.filter(post => post.id !== postId));
      } catch (error) {
        console.error('Error deleting post:', error);
      }
    };

  return (
    <div>
      {/* Header */}
      <div style={{ height: '8vh', width: '100%', background: 'white', display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '0 5vw' }}>
        <img src={logo} alt='Logo' style={{ maxHeight: '6vh' }} />
        <span onClick={() => navigate('/account')} style={{ cursor: 'pointer' }}>Mon compte</span>
      </div>

      {/* Banner */}
      <div style={{ width: '100%', height: '10vh', background: 'lightgrey', display: 'flex', justifyContent: 'center', alignItems: 'center' }}>
        <h1 style={{ color: 'red', margin: 0 }}>Bienvenue dans mon forum GW2</h1>
      </div>

      {/* Discussions Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '5vh', marginBottom: '5vh', marginLeft: '5vw', marginRight: '5vw' }}> {/* Updated margin bottom */}
                <h2 style={{ margin: 0 }}>Discussions</h2>
                <span onClick={navigateToAccount} style={{ color: 'red', fontSize: '24px', cursor: 'pointer' }}>＋</span>
            </div>

      {/* Posts */}
      {posts.map((post, index) => (
                <div key={post.id} style={{ marginLeft: '10vw', marginRight: '10vw' }}>
        {/* Post content */}
        <h3 style={{ color: 'red', cursor: 'pointer' }} onClick={() => navigate(`/posts/${post.id}`)}>
            {post.title}
        </h3>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
            <p style={{ color: '#000' }}>{post.content.substring(0, 100)}...</p>
            <span style={{ color: 'lightgrey', marginRight: '5vw' }}>{post.userName} / {post.gw2_account_name}</span>
        </div>
        {/* Delete Button for Post Creator */}
        {currentUserId && post.user_id === currentUserId && (
                        <button 
                            onClick={() => handleDeletePost(post.id)} 
                            style={{ backgroundColor: 'transparent', color: 'red', border: 'none', cursor: 'pointer', fontSize: '20px' }}
                            title="Delete Post"
                        >
                            ❌
                        </button>
                    )}
                    {index !== posts.length - 1 && (
                        <hr style={{ borderColor: 'red', width: 'calc(50% - 10vw)', marginLeft: '10vw', marginTop: '10px', marginBottom: '10px' }} />
                    )}
                </div>
            ))}


      {/* Footer */}
      <div style={{ width: '100%', background: 'red', color: 'white', textAlign: 'center', padding: '1vh 0', marginTop: 'auto' }}> {/* Updated for sticking to bottom */}
                {/* Footer content */}
                <p>Contact: lpeyramaure@eduvaud.ch</p>
                <p>Nombre d'inscrits: 451</p>
                <p>Nombre de posts: 645</p>
            </div>
        </div>
    );
}

export default DisplayPosts;
